import useFetch from "../../hooks/userFetch";
import "./featured.css";

const Featured = () => {
  const { data, loading, error } = useFetch(
    "/event/countByCity?cities=jaipur,mumbai,delhi,pune"
  );
  const cities = [
    "Jaipur",
    "Delhi",
    "Mumbai",
    "Pune",
    "Kota",
    "Chennai",
    "Bangalore",
    "Kolkata",
  ];

  return (
    <div>
      {loading ? (
        "loading..."
      ) : (
        <>
          <div id="cards_landscape_wrap-2">
            <h1>Want to find college fests in your city?</h1>
            <h5>Don't worry CampFest is the place for you</h5>
            <div className="container">
              <div className="row">
                {data &&
                  cities.map((img, i) => (
                    <div className="col-xs-12 col-sm-6 col-md-3 col-lg-3">
                      <a href="">
                        <div className="card-flyer">
                          <div className="text-box">
                            <div className="image-box">
                              <img
                                src="https://img.traveltriangle.com/blog/wp-content/uploads/2019/11/cover-image-of-jaipur-in-march_22nd-nov.jpg"
                                alt=""
                              />
                            </div>
                            <div className="text-container">
                              <h6>{img}</h6>
                              <p>{data[i]} College Fests</p>
                            </div>
                          </div>
                        </div>
                      </a>
                    </div>
                  ))}
              </div>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default Featured;
