import { useState } from "react";
import { useLocation } from "react-router-dom";
import Header from "../../components/header/Header";
import Navbar from "../../components/navbar/Navbar";
import SearchItems from "../../components/searchItems/SearchItems";
import "./list.css";
import useFetch from "../../hooks/userFetch";

const List = () => {
  const fest = useLocation();
  const [festName, setFestName] = useState(fest.state.festName);
  const [collegeName, setCollegeName] = useState(fest.state.collegeName);
  const [cityName, setCityName] = useState(fest.state.cityName);

  const pramName =
    festName === "" ? (collegeName === "" ? "city" : "college") : "title";
  const pram =
    festName === "" ? (collegeName === "" ? cityName : collegeName) : festName;
  const { data, loading, error, refetch } = useFetch(
    `/event?${pramName}=${pram}`
  );

  const handleClick = () => {
    pramName =
      festName === "" ? (collegeName === "" ? "city" : "college") : "title";
    pram =
      festName === ""
        ? collegeName === ""
          ? cityName
          : collegeName
        : festName;
    refetch();
  };

  return (
    <div>
      <Navbar />
      <Header type="list" />
      <div className="listContainer">
        <div className="listWrapper">
          <div className="listSearch">
            <h1 className="listTitle">Search</h1>
            <div className="listItem">
              <label>Event</label>
              <input
                type="text"
                placeholder={festName}
                onChange={(e) => setFestName(e.target.value)}
              ></input>
            </div>
            <div className="listItem">
              <label>College</label>
              <input
                type="text"
                placeholder={collegeName}
                onChange={(e) => setCollegeName(e.target.value)}
              ></input>
            </div>
            <div className="listItem">
              <label>City</label>
              <input
                type="text"
                placeholder={cityName}
                onChange={(e) => setCityName(e.target.value)}
              ></input>
            </div>
            <button onClick={handleClick}>Search</button>
          </div>

          <div className="listResult">
            {loading ? (
              "loading"
            ) : (
              <>
                {data.map((item) => (
                  <SearchItems item={item} key={item._id} />
                ))}
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default List;
